import React, {useContext} from 'react'
import { AppContext, useGlobalContext } from './context';


const Home = () => {
    const {openSidebar, closeSidebar, isSidebarOpen, openModal} = useGlobalContext();

    return (
        <>
<button onClick={openModal}>open modal</button>
<br></br>
<button onClick={isSidebarOpen ? openSidebar : closeSidebar}>open sidebar</button>
</>
    )
}

export default Home;
