import React, {useState} from 'react';


function Question({id, answer, question}) {

const [show, setShow] = useState(false);

const handleClick = () => {
setShow(!show)
}
  return (
    <> 
<div className='question' id={id}>{question}

</div>
 <div >
  <button className='button' id={id} onClick={() => handleClick}  >Expand</button> 

  </div>
{show && <div >{answer}</div>}

</>

  )
}

export default Question;
