import React, {useState , useEffect} from 'react';
import './styles/App.css';


const url = 'https://random-data-api.com/api/users/random_user?size=6 '


function App() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true)
  const [value, setValue] = useState(0)


const fetchUsers = async() => {
  try {

  const response = await fetch (url) ;
  const newUsers = await response.json();
  setUsers(newUsers)
setLoading(false)

  }
  catch (error) {
console.log(error)
  }
}

useEffect(() => {
  fetchUsers()
 } , []);

 useEffect(() => {
   setValue(value + 1)
  fetchUsers()
 } , [value]);

if (loading)  {
return (<div>
  loading..
</div>) }

// increments or decrements depending on which button clicked
const  incOrDec = (type) =>{
if ((type == 'inc') && (value <= (users.length-2))) {
  setValue(value + 1)
}
else if ((type == 'inc') && (value  > (users.length-2))){
setValue(0) }

if ((type == 'dec') && (value > 0)) {
  setValue(value - 1)
}
else if ((type == 'dec') && (value == 0)) {
  setValue(5)
}
}

return (
    <>  <header>
    ugly basic slider project - fetches 6 random users from API, displays them in slide, controlled with left/right arrows
  </header>
<br></br>
  <div>this continues my attempts to make barebones versions of projects from John Smilga's React Tutorial with little to no help/reference. for this one, i managed to do it with zero help!! </div>

<div className="container">

<div>{users[value].first_name}{users[value].last_name}</div>
  </div>
  <button className='button' onClick={()=>(incOrDec('dec'))}> left </button>
<button className='button' onClick={()=>(incOrDec('inc'))}> right </button>

 <div className="all-users">here all all the users to choose from this time (but you can't click these names):
 {users.map((user, index)=> {

 return <div> 
    {user.first_name}     {user.last_name} </div>})}</div>


</>
)}



export default App;
